// src/App.tsx
import React, { useState, useRef } from "react";
import { Filter } from "./types/filter"; // „Éï„Ç£„É´„Çø„Éº„ÅÆÂûãÂÆöÁæ©
import TodoList from "./components/TodoList"; // TodoList„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
import StyledInput from "./components/StyledInput"; // „Çπ„Çø„Ç§„É™„É≥„Ç∞„Åï„Çå„ÅüÂÖ•Âäõ„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
import StyledButton from "./components/StyledButton"; // „Çπ„Çø„Ç§„É™„É≥„Ç∞„Åï„Çå„Åü„Éú„Çø„É≥„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
import StyledPopup from "./components/StyledPopup"; // „Çπ„Çø„Ç§„É™„É≥„Ç∞„Åï„Çå„Åü„Éù„ÉÉ„Éó„Ç¢„ÉÉ„Éó„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
import StyledSearchIcon from "./components/StyledSearchIcon"; // "üîç"„Çπ„Çø„Ç§„É™„É≥„Ç∞„Åï„Çå„ÅüËô´ÁúºÈè°„Ç¢„Ç§„Ç≥„É≥„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
import StyledClearButton from "./components/StyledClearButton"; // "√ó"„Çπ„Çø„Ç§„É™„É≥„Ç∞„Åï„Çå„Åü„ÇØ„É™„Ç¢„Éú„Çø„É≥„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà

import { GlobalStyle } from "./styles/GlobalStyle"; // „Ç∞„É≠„Éº„Éê„É´„Çπ„Çø„Ç§„É´
import { useTodos } from "./hooks/useTodos";

const App: React.FC = () => {
  const [text, setText] = useState(""); // ÂÖ•Âäõ‰∏≠„ÅÆ„ÉÜ„Ç≠„Çπ„Éà
  const inputRef = useRef<HTMLInputElement>(null); // „ÉÜ„Ç≠„Çπ„ÉàÂÖ•Âäõ„ÅÆref

  const {
    filteredTodos,
    sortedTodos,
    filter,
    setFilter,
    addTodo,
    deleteTodo,
    toggleTodo,
    updateTodo,
    deleteChecked,
    checkAll,
    uncheckAll,
    checkedCount,
    showPopup,
    setShowPopup,
    handleSort,
    sortKey,
    sortOrder,
    searchText,
    setSearchText,
  } = useTodos();

  return (
    <>
      <GlobalStyle />

      <div>
        {/* „Çø„Ç§„Éà„É´ */}
        <h1>TODO List</h1>

        <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
          {/* „Éï„Ç£„É´„Çø„Éº„ÄÄÂÖ®‰ª∂ / ÂÆå‰∫Ü / Êú™ÂÆå‰∫Ü */}
          „Éï„Ç£„É´„Çø„ÉºÔºö
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value as Filter)}
          >
            <option value="all">ÂÖ®‰ª∂</option>
            <option value="completed">ÂÆå‰∫Ü„ÅÆ„Åø</option>
            <option value="incomplete">Êú™ÂÆå‰∫Ü„ÅÆ„Åø</option>
          </select>
          <div style={{ position: "relative", display: "inline-block" }}>
            {/* Ëô´ÁúºÈè° */}
            <StyledSearchIcon>üîç</StyledSearchIcon>

            {/* Ê§úÁ¥¢ */}
            <StyledInput
              ref={inputRef}
              type="text"
              placeholder="ÂÜÖÂÆπ„ÅßÊ§úÁ¥¢"
              value={searchText}
              onChange={(e) => setSearchText(e.target.value)}
              style={{ paddingRight: "24px" }} // Âè≥ÂÅ¥„Å´‰ΩôÁôΩ„Çí‰Ωú„Çã
            />

            {/* √ó„Éú„Çø„É≥ */}
            {searchText && (
              <StyledClearButton
                onClick={() => {
                  setSearchText("");
                  inputRef.current?.focus(); // „Éï„Ç©„Éº„Ç´„Çπ„ÇíÊàª„Åô
                }}
              >
                √ó
              </StyledClearButton>
            )}
          </div>
        </div>

        <div>
          <div style={{ position: "relative", display: "inline-block" }}>
            {/* ËøΩÂä† */}
            <StyledInput
              ref={inputRef}
              aria-label="Êñ∞„Åó„ÅÑTODO„ÇíÂÖ•Âäõ"
              value={text}
              onChange={(e) => setText(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === "Enter" && text.trim() !== "") {
                  addTodo(text.trim());
                  setText("");
                }
              }}
              style={{ paddingRight: "24px" }} // Âè≥ÂÅ¥„Å´‰ΩôÁôΩ„Çí‰Ωú„Çã
              placeholder="Êñ∞„Åó„ÅÑTODO„ÇíÂÖ•Âäõ"
            />
            {/* √ó„Éú„Çø„É≥ */}
            {text && (
              <StyledClearButton onClick={() => {
                setText("")
                inputRef.current?.focus(); // „Éï„Ç©„Éº„Ç´„Çπ„ÇíÊàª„Åô
                }}  >
                √ó
              </StyledClearButton>
            )}
          </div>

          {/* ËøΩÂä†„Éú„Çø„É≥ */}
          <StyledButton
            onClick={() => {
              if (text.trim() === "") return; // Á©∫ÊñáÂ≠ó„ÅØËøΩÂä†„Åó„Å™„ÅÑ
              addTodo(text); // TODO„ÇíËøΩÂä†
              setText(""); // ÂÖ•ÂäõÊ¨Ñ„Çí„ÇØ„É™„Ç¢ ‚Üê „Åì„Çå„Åå„Éù„Ç§„É≥„ÉàÔºÅ
            }}
          >
            ËøΩÂä†
          </StyledButton>

          {/* ‰∏ÄÊã¨ÂâäÈô§„Éú„Çø„É≥ ÂâäÈô§‰ª∂Êï∞Ôºû0„ÅÆÂ†¥Âêà„ÅÆ„Åø‰∏ÄÊã¨„Éú„Çø„É≥„ÇíË°®Á§∫„Åô„Çã„ÄÇ*/}
          {checkedCount > 0 && (
            <StyledButton
              onClick={() => setShowPopup(true)}
              title="ÂÆå‰∫Ü„Åß„ÉÅ„Çß„ÉÉ„ÇØÊ∏à„ÅÆË°å„ÇíÂâäÈô§„Åó„Åæ„Åô"
            >
              ‰∏ÄÊã¨ÂâäÈô§ ÂØæË±°Ôºö{checkedCount}‰ª∂
            </StyledButton>
          )}

          {/* „Éù„ÉÉ„Éó„Ç¢„ÉÉ„Éó„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
        ‰∏ÄÊã¨ÂâäÈô§„Éú„Çø„É≥„ÇØ„É™„ÉÉ„ÇØÊôÇ„Å´Ë°®Á§∫„Åï„Çå„Åæ„Åô */}
          {showPopup && (
            <StyledPopup>
              <p>ÂÆå‰∫ÜÊ∏à„Åø„ÅÆ„Çø„Çπ„ÇØ„Çí„Åô„Åπ„Å¶ÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü</p>
              <StyledButton onClick={() => deleteChecked()}>„ÅØ„ÅÑ</StyledButton>
              <StyledButton onClick={() => setShowPopup(false)}>
                „ÅÑ„ÅÑ„Åà
              </StyledButton>
            </StyledPopup>
          )}
        </div>

        {/* ‰∏ÄË¶ß */}
        <TodoList
          filteredTodos={filteredTodos}
          sortedTodos={sortedTodos}
          deleteTodo={deleteTodo}
          toggleTodo={toggleTodo}
          updateTodo={updateTodo}
          checkedCount={checkedCount}
          checkAll={checkAll}
          uncheckAll={uncheckAll}
          handleSort={handleSort}
          sortKey={sortKey}
          sortOrder={sortOrder}
        />
      </div>
    </>
  );
};

export default App;
