import styled from "styled-components";

const StyledTableHeader = styled.th`
  border-bottom: 2px solid #ccc; // 下線のスタイル
  background-color: #f5f5f5; // 背景色
  padding: 8px; // 内側の余白
  text-align: left; // テキストの配置
`;
export default StyledTableHeader;
