import styled from "styled-components";

const StyledPopup = styled.div`
  background: #fff;
  border: 1px solid #ccc;
  padding: 10px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

export default StyledPopup;
