import styled from "styled-components";

const StyledButton = styled.button`
  margin: 5px;
  padding: 8px 12px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;

  &:hover {
    background: #0056b3;
  }
`;
export default StyledButton;
